import React,{Component} from "react";
class Mainpage extends Component{
     state={
       age:'',
       gender:'',
       hval:'',
       wval:'',
       sbp:'',
       sugar:'',
       dbp:'',
       result:'',
       preg:false,
       bloodSugarType:''
